sys_graphql_schema.schema:
schema {
	query: Query
}

type Query {
	getAllContents: [Content!],
	getAllLabels: [Label!]
}

type Content {
	id: ID @source(value: "sys_id.value"),
	title: String @source(value: "title.value"),
	description: String @source(value: "description.value")
	views: Int @source(value: "views.value"),
	likes: Int @source(value: "likes.value"),
	active: Boolean @source(value: "active.display_value"),
	tags: [Tag!]
}

type Tag {
	id: ID @source(value: "sys_id.value"),
	label: Label @source(value: "label.value"),
	count: Int @source(value: "count.value"),
	content: Content @source(value: "content.value")
}

type Label {
	id: ID @source(value: "sys_id.value"),
	name: String @source(value: "name.value"),
	category: Category @source(value: "category.value")
}

enum Category{
	positive,
	neutral,
	negative
}
